export interface NativePreferenceBridgeContract {
    getString(preferenceName: string, key: string, defaultValue?: string): Promise<string>;
    getNumber(preferenceName: string, key: string, defaultValue: number): Promise<number>;
    getDecryptedUserId(): Promise<string>;
    saveNumber(preferenceName: string, key: string, value: number): void;
    saveString(preferenceName: string, key: string, value: string): void;
    removeItem(preferenceName: string, key: string): void;
}
